const fs = require('fs');

const readLinesAsArray = filename => fs.readFileSync(filename).toString().split('\n').filter(Boolean);

const sum = (...arr) => arr.reduce((acc, v) => {
  if (isNaN(v)) {
    throw Error(`${v} is not a number`);
  }
  return acc+=v;
}, 0);

const product = (...arr) => arr.reduce((acc, v) => {
  if (isNaN(v)) {
    throw Error(`${v} is not a number`);
  }
  return acc*=v;
}, 1);

module.exports = {
  product,
  readLinesAsArray,
  sum,
};
